mapper.select:

select seckill_id, seckill_name, stock_count, create_time, start_time, finish_time
from seckill
where seckill_id = #{seckillId,jdbcType=INTEGER}

select seckill_id, seckill_name, stock_count, create_time, start_time, finish_time
from seckill
where finish_time > #{currentTime,jdbcType=TIMESTAMP}

mapper.update:
  update seckill
  set stock_count = (stock_count - 1)
  where seckill_id = #{seckillId,jdbcType=INTEGER}
		  and stock_count > 0
 		  and   finish_time >= #{currentTime,jdbcType=TIMESTAMP}

update seckill
set stock_count = #{stockCount,jdbcType=INTEGER},
where seckill_id = #{seckillId,jdbcType=INTEGER}
